export const Bars = styled(FaBars)`
 display: none;
 color: #000;

 @media screen and (max-width: 768px) {
     display: block;
     position: absolute;
     top: 0;
     right: 0;
     transform: translate(-100%, 75%);
     font-size: 1.8rem;
     cursor: pointer;
 }
`

export const NavMenu = styled.div`
 display: flex;
 align-items: center;
 margin-right: -24px;

 @media screen and (max-width: 768px) {
     display: none;
 }
`
