import styled from 'styled-components';
import { NavLink as Link } from 'react-router-dom';
import {FaBars} from 'react-icons/fa';

export const Nav = styled.nav`
 background: #fff;
 height: 80px;
 margin-top: -80px;
 display: flex;
 justify-content: center;
 align-items: center;
 font-size: 1rem;
 position: sticky;
 top: 0;
 z-index: 10;

 @media screen and (max-width: 960px) {
     transition: 0.8s all ease;
 }
`

export const NavbarContainer = stlyed.div

export const NavLink = styled(Link)`
color: #000;
letter-spacing: .2rem;
display: flex;
align-items: center;
text-decoration: none;
padding: 0 2rem;
height: 100%;
cursor: pointer;
margin-top: 15px;
margin-left: -20px;
`

export const Bars = styled(FaBars)`
 display: none;
 color: #000;

 @media screen and (max-width: 768px) {
     display: block;
     position: absolute;
     top: 0;
     right: 0;
     transform: translate(-100%, 75%);
     font-size: 1.8rem;
     cursor: pointer;
 }
`

export const NavMenu = styled.div`
 display: flex;
 align-items: center;
 margin-right: -24px;

 @media screen and (max-width: 768px) {
     display: none;
 }
`
